const Experience = ({ job }) => {
    console.log(job)
  const dutiesCompany = [];
  const technologies = [];

  for (const [index, value] of job.duties) {
    dutiesCompany.push(<li key={index}>{value}</li>);
  }

  for (const [index, value] of job.technologiesUsed) {
    technologies.push(
      <li key={index} class="list-inline-item">
        <span class="badge badge-primary badge-pill">{value}</span>
      </li>
    );
  }

  return (
    <div class="resume-timeline position-relative">
      <article class="resume-timeline-item position-relative pb-5">
        <div class="resume-timeline-item-header mb-2">
          <div class="d-flex flex-column flex-md-row">
            <h3 class="resume-position-title font-weight-bold mb-1">
              {job.role}
            </h3>
          </div>
          <p>
            <a href={job.company.url} target="_blank">
              {job.company.name}
            </a>
          </p>
          <div class="resume-position-time">
            {job.date.from} - {job.date.to}
          </div>
        </div>
        <div class="resume-timeline-item-desc">
          <ul>{items}</ul>
          <ul class="list-inline">{technologies}</ul>
        </div>
      </article>
    </div>
  );
};

export default Experience;
